mapper.select:
(SELECT t.id ,t.biz_code ,t.ref_type ,t.ref_order ,t.deal_node
,t.deal_note ,t.operate_role ,t.operator ,t.operator_name ,t.operator_mobile
,t.start_datetime ,t.end_datetime ,t.speed_time FROM tsys_biz_log t INNER JOIN tsys_role_node trn ON t.deal_node = trn.node_code left join tdq_budget_order b on b.code = t.parent_order where trn.role_code = #{roleCode} and b.is_logistics = #{isLogistics}
AND t.ref_type = #{refType}
AND t.biz_code = #{bizCode}
order by t.start_datetime desc) union (SELECT t.id ,t.biz_code ,t.ref_type ,t.ref_order ,t.deal_node
,t.deal_note ,t.operate_role ,t.operator ,t.operator_name ,t.operator_mobile
,t.start_datetime ,t.end_datetime ,t.speed_time FROM tsys_biz_log t INNER JOIN tsys_role_node trn ON t.deal_node = trn.node_code where trn.role_code = #{roleCode} and t.ref_type = '012'
AND t.ref_type = #{refType}
AND t.biz_code = #{bizCode}
order by t.start_datetime desc) union (SELECT t.id ,t.biz_code ,t.ref_type ,t.ref_order ,t.deal_node
,t.deal_note ,t.operate_role ,t.operator ,t.operator_name ,t.operator_mobile
,t.start_datetime ,t.end_datetime ,t.speed_time FROM tsys_biz_log t INNER JOIN tsys_role_node trn ON t.deal_node = trn.node_code left join tdq_credit c on t.parent_order = c.code where trn.role_code = #{roleCode} and t.ref_type = '001'
AND t.ref_type = #{refType}
AND t.biz_code = #{bizCode}
order by t.start_datetime desc)

select count(*) from ((SELECT t.id FROM tsys_biz_log t INNER JOIN tsys_role_node trn ON
t.deal_node = trn.node_code left join tdq_budget_order b on b.code = t.ref_order where
trn.role_code = #{roleCode} and b.is_logistics = #{isLogistics}

    AND t.ref_type = #{refType}


    AND t.ref_order = #{refOrder}

)
union
(SELECT t.id FROM tsys_biz_log t INNER JOIN tsys_role_node trn ON t.deal_node =
trn.node_code where trn.role_code = #{roleCode} and t.ref_type = '012'

    AND t.ref_type = #{refType}


    AND t.ref_order = #{refOrder}

)
union
(SELECT t.id FROM tsys_biz_log t INNER JOIN tsys_role_node trn ON t.deal_node =
trn.node_code left join tdq_credit c on t.ref_order = c.code where trn.role_code =
#{roleCode} and t.ref_type = '001'

    AND t.ref_type = #{refType}


    AND t.ref_order = #{refOrder}

)) a

mapper.update:
UPDATE tsys_biz_log
SET
biz_code = #{bizCode}
,ref_type = #{refType}
,ref_order = #{refOrder}
,deal_node = #{dealNode}

,deal_note = #{dealNote}
,operate_role = #{operateRole}
,operator = #{operator}
,operator_name = #{operatorName}
,operator_mobile = #{operatorMobile}

,start_datetime = #{startDatetime}
,end_datetime = #{endDatetime}
,speed_time = #{speedTime}
WHERE id = #{id}

UPDATE tsys_biz_log
SET
end_datetime = #{endDatetime}
,speed_time = #{speedTime}
WHERE id = #{id}
